import React, { useState } from 'react';
import ReCAPTCHA from 'react-google-recaptcha';
import { toast, ToastContainer } from 'react-toastify';
import { ContainerSucces } from './ContactStyles'; // Corrected import
import 'react-toastify/dist/ReactToastify.css';

const Contact = () => {
  const [isHuman, setIsHuman] = useState(true);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [submitted, setSubmitted] = useState(false); // Added state to track submission

  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append("name", name);
    formData.append("email", email);
    formData.append("message", message);

    try {
      const response = await fetch("https://7cb11f0196db781a064b153f4af36d9a.m.pipedream.net", {
        method: "POST",
        body: formData
      });

      if (response.ok) {
        alert("Form submitted successfully!");

        toast.success('Email successfully sent!', {
          position: toast.POSITION.BOTTOM_LEFT,
          pauseOnFocusLoss: false,
          closeOnClick: true,
          hideProgressBar: false,
          toastId: 'succeeded',
        });
        setSubmitted(true); // Set submitted state to true

        setName('');
        setEmail('');
        setMessage('');
      } else {
        toast.error("Error submitting form");
      }
    } catch (error) {
      console.error("Error:", error);
      toast.error("Error submitting form");
    }
  };

  return submitted ? (
    // Render success message when submitted is true
    <ContainerSucces>
      <h3>Thanks for getting in touch!</h3>
      <button
        onClick={() => {
          window.scrollTo({ top: 0, behavior: 'smooth' });
          setSubmitted(false); // Reset form after success message
        }}
      >
        Back to the top
      </button>
      <ToastContainer />
    </ContainerSucces>
  ) : (
    // Render form if not submitted
    <section className="contact section" id="contact">
      <h2 className="section-title">Contact</h2>
      <div className="contact__container bd-grid">
        <form id="contactForm" onSubmit={handleSubmit} className="contact__form">
          <input
            type="text"
            id="name"
            placeholder="Name"
            className="contact__input"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
          <input
            type="email"
            id="email"
            placeholder="Email"
            className="contact__input"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <textarea
            id="message"
            placeholder="Message"
            className="contact__input"
            rows="10"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            required
          ></textarea>
          <ReCAPTCHA
            align="center"
            sitekey="6Lf15nwqAAAAAFPGUOE5N9U-TaoXhPS3iE7vujFo"
            onChange={() => setIsHuman(true)}
          />
          <button type="submit" disabled={!message || !isHuman} className="contact__button button">
            Submit
          </button>
        </form>
      </div>
      <ToastContainer />
    </section>
  );
};

export default Contact;
