import React, { useState } from 'react';
import ReCAPTCHA from 'react-google-recaptcha';

const Contact = () => {
  const [isHuman, setIsHuman] = useState(false)
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');


  const handleSubmit = async (event) => {
    event.preventDefault(); // Prevent the default form submission

    const formData = new FormData();
    formData.append("name", name);
    formData.append("email", email);
    formData.append("message", message);

    try {
      const response = await fetch("https://7cb11f0196db781a064b153f4af36d9a.m.pipedream.net", {
        method: "POST",
        body: formData
      });

      if (response.ok) {
        alert("Form submitted successfully!");
        // Clear the form after successful submission
        setName('');
        setEmail('');
        setMessage('');
      } else {
        alert("Error submitting form");
      }
    } catch (error) {
      console.error("Error:", error);
      alert("Error submitting form");
    }
  };

  return (
    <section className="contact section" id="contact">
      <h2 className="section-title">Contact</h2>

      <div className="contact__container bd-grid">
        <form id="contactForm" onSubmit={handleSubmit} className="contact__form">
          <input
            type="text"
            id="name"
            placeholder="Name"
            className="contact__input"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
          <input
            type="email"
            id="email"
            placeholder="Email"
            className="contact__input"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <textarea
            id="message"
            placeholder="Message"
            className="contact__input"
            rows="10"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            required
          ></textarea>
          <ReCAPTCHA
          sitekey="6LeIxAcTAAAAAJcZVRqyHh71UMIEGNQ_MXjiZKhI"
          onChange={(e) => {
            setIsHuman(true)
          }} 
        ></ReCAPTCHA>

          <button type="submit" disabled={!message || !isHuman} className="contact__button button">Submit</button>
        </form>
      </div>
    </section>
  );
};

export default Contact;
